package tests;

public class DataTypesTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// JAVA EXECUTE LINE BY LINE BUT TAKE THE LATEST VALUE
		//int
		int i = 10;
		System.out.println(i);

		i = 20;
		System.out.println(i);
		i=i+10;
		System.out.println(i);
		i=i*2;
		System.out.println(i);
		System.out.println(i*2);
		System.out.println(i);
		
		// i++ = Its post increment so it will increase i value by 1. So line 22 will be 60 but line 23 will be 61
		//++i = Its pre increment so it will both the line 23 and 24
		System.out.println(++i);
		System.out.println(i);

		//double
		double d = 10.6;
		System.out.println(d);
		System.out.println(d*2);
		
		//char
		char a = 'b';
		System.out.println(a);
		char e = '8';
		System.out.println(e);
	
		//boolean
		boolean b = true;
		System.out.println(b);
		b = false;
		System.out.println(b);
	
			
	}

}
